$(document).ready(function() {
	$(".comments__slider").slick({
		centerMode: true,
        arrows: false,
		adaptiveHeight: true,
		speed: 500,
		initialSlide: 2,
		autoplay: false,
		variableWidth: true,
		responsive: [
			{
				breakpoint: 700,
				settings: {
					centerMode: true,
					arrows: false,
					adaptiveHeight: true,
					speed: 500,
					initialSlide: 2,
					autoplay: false,
					variableWidth: false,
				}
			}
		]
	});
    $('.comments__slider_prev').click(function(event) {
        $('.comments__slider').slick('slickPrev');
    });
    $('.comments__slider_next').click(function(event) {
        $('.comments__slider').slick('slickNext');
    });

	//validation form
	function validateForms(form){
        $(form).validate({
            rules: {
                name: {
                    required: true,
                },
                tel: "required",
                email: {
                    required: true,
                    email: true
                }
            },
            messages: {
                name: "",
                tel: "",
                email: "",
            }
        });
    };
    validateForms('#consultation-form');
    validateForms('#form-questions');
	validateForms("#model-call-me");

	//mask phone
	$.fn.setCursorPosition = function(pos) {
		if ($(this).get(0).setSelectionRange) {
		  $(this).get(0).setSelectionRange(pos, pos);
		} else if ($(this).get(0).createTextRange) {
		  var range = $(this).get(0).createTextRange();
		  range.collapse(true);
		  range.moveEnd('character', pos);
		  range.moveStart('character', pos);
		  range.select();
		}
	  };
	  $("input[name=tel]").click(function(){
		$(this).setCursorPosition(3);
	  }).mask("+7(999) 999-9999");
	  $("input[name=tel]").mask("+7(999) 999-99-99");

	//hamburger
	$("#hamburger, .mobile-nav__click, .mobile-nav__item, .blackout-fon, .mobile-nav__main").click(function(){
		$(".mobile-nav").toggleClass("mobile-nav--active");
		$(".header-mobile").toggleClass("header-mobile--active");
		$(".header--top").toggleClass("header--top--active");
		$(".blackout-fon").toggleClass("blackout-fon--active");
		$(".header-mobile__hamburger").toggleClass("header-mobile__hamburger--active");
		$("body").toggleClass("active");
	});

	 //model-call-me
	$('[data-model=model-call-me]').on('click', function() {
		$('#model').fadeIn(200);
		$('#overlay').fadeIn(100);
	});

	$('.model__close, #overlay').on('click', function() {
		$('#overlay').fadeOut(300);
		$("#model").fadeOut(150);
	});
	
	//scroll smooth
	$(".mobile-nav, .header").on("click","a[href^='#']", function (event) {
		event.preventDefault();
		var div = $(this).attr('href');
		var top = $(div).offset().top;
		var pos = (window.pageYOffset !== undefined) ? window.pageYOffset : (document.documentElement || document.body.parentNode || document.body).scrollTop;
	
		var delta = (top > pos) ? 40 : 40;
	  
		top = top-delta;
		$('body,html').animate({scrollTop: top}, 50);
	  });
});

//header fixed
$(window).scroll(function() {
	var scroll = $(window).scrollTop();

	if (scroll >= 200) {
		$(".header").addClass("header--top"),
		$(".header__wrapper_logo").addClass("header--top"),
		$(".nav__link").addClass("header--top"),
		$(".header-mobile").addClass("header--top");
	} else {
		$(".header").removeClass("header--top"),
		$(".header__wrapper_logo").removeClass("header--top"),
		$(".nav__link").removeClass("header--top"),
		$(".header-mobile").removeClass("header--top");
	};
});

$(window).scroll(function() {
	var scroll = $(window).scrollTop();

	if (scroll >= 300) {
		$(".header").addClass("fixed"),
		$(".header-mobile").addClass("fixed");
	} else {
		$(".header").removeClass("fixed"),
		$(".header-mobile").removeClass("fixed");
	}
});

$(window).scroll(function() {
	var scroll = $(window).scrollTop();

	if (scroll >= 525) {
		$(".header").addClass("header--fixed"),
		$(".header-mobile").addClass("header--fixed");
	} else {
		$(".header").removeClass("header--fixed"),
		$(".header-mobile").removeClass("header--fixed");
	}
});

//google maps api
function initMap() {
	const element = document.getElementById('map');
	const options = {
		zoom: 17,
		center: {lat: 55.748031535978114, lng: 37.62730509959978}
	};

	const myMap = new google.maps.Map(element, options);

	const marker = new google.maps.Marker({
		position: {lat: 55.748031535978114, lng: 37.62730509959978},
		map: myMap,
		icon: 'img/global-opt-map.png'
	});

	var InfoWindow = new google.maps.InfoWindow({
		content: '<h1 class="map-city">г. Москва</h1> <p class="map-text">ул. Садовническая, дом 5, офис 4-6 700 от м. Новокузнецкая <div>Тел: +7 (926) 423 01 00</div> <a href="#" class="map-link">info@glopt.ru</a></p>',
		pixelOffset: new google.maps.Size(170, 100),
		maxWidth: '500px'
	});

	InfoWindow.open(myMap, marker);

	marker.addListener('click', function() {
		InfoWindow.open(myMap, marker);
	})
};

